import React, {Component} from 'react'
import axios from "axios";

 export class EventHangouts extends Component{
     state = {
         items: []
     };
        constructor(){
            super();
            axios.get('http://localhost:4449/getUsers').then(res=>{
                console.log(res.data)
                this.setState({items: res.data})
            })
        }
render() {
            return (
                <div>
                    <p>This is the description of the hangout.</p>
                    <label>Name:</label>
                    <input type="text"/>
                    <table>
                        <thead>
                        <tr>
                            <th></th>
                            <th>19,sep</th>
                            <th>20,sep</th>
                            <th>21,sep</th>
                            <th>22,sep</th>
                            <th>23,sep</th>
                        </tr>
                        </thead>
                        <tbody>
                        {this.state.items.map(item => (
                            <tr>
                                <td>{item.name}</td>
                                <td>{item.date}</td>
                                <td>{item.time}</td>
                                <td><input type="checkbox" /></td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                    <button>Done</button>
                </div>
            );
        }
}
