import React from 'react';
import './NewHangouts.css';
import {Link} from 'react-router-dom';

export const NewHangouts = () => {
    var i=1;

    const addEventDates = async() => {
    debugger;
             while(i>0){
             var parent = document.getElementById('hangoutSchedule'+i);
             debugger;
             var dateValue=document.querySelectorAll('#hangoutSchedule1 input[type="date"]')[0].value;
var timeValue=document.querySelectorAll('#hangoutSchedule1 input[type="time"]')[0].value;
                 const response = await fetch("http://localhost:4449/setEventDates?eventDate={dateValue}&eventTime={timeValue}")
                 const json = await response.json()
                 console.log(json);
                 i--;
             }
         }

    const addElements = () => {
      var original = document.getElementById('hangoutSchedule'+i);
      if(original!=null&&i<5){
        var clone = original.cloneNode(true);
         clone.id = 'hangoutSchedule'+ ++i;
         document.getElementById('schedules').appendChild(clone);
         }
}

    return (
        <div className="newHangouts">
            <label>New Hangouts:<br/> <p>This is the description of the hangout.</p></label>
            <div id="schedules">
            <div id="hangoutSchedule1">
            <label>Date:</label>
            <input type="date"/>
            <label>Time:</label>
            <input type="time"/>
            </div>
            </div>
            <button onClick={addElements}>Add another option</button>
            <Link onClick={addEventDates} to="/eventHangouts" >Done</Link>
        </div>
    );
}
