import React from 'react';
import './NewHangouts.css';
import {Link} from 'react-router-dom';

export const NewHangouts = () => {
    function clickAction(){
        const element = document.createElement("a");
        const file = new Blob(['Pizza Price: '+menu_store.pizzaPrice+ '\n'
        +'Pizza Tax: '+menu_store.pizzaTax+'\n'
        +'Delivery Charge: '+menu_store.pizzaDeliveryCharge+'\n'
        +'========================================'
        +'Total: '+menu_store.pizzaTotalPrice+'\n'
        ], {type: 'text/plain'});
        element.href = URL.createObjectURL(file);
        element.download = "order.txt";
        document.body.appendChild(element); // Required for this to work in FireFox
        element.click();
    }
    
    return (
        <div className="newHangouts">
            <label>New Hangouts:<br/> <p>This is the description of the hangout.</p></label>
            <div className="hangoutSchedule">
            <label>Date:</label>
            <input type="date"/>
            <label>Time:</label>
            <input type="time"/>
            </div>
            <button on>Add another option</button>
            <Link to="/eventHangouts">Done</Link>
        </div>
    );
}
