import React, { useState,useEffect } from 'react'
import axios from "axios";
import {Link} from 'react-router-dom';

 export const EventHangouts =()=>{
     const [error, setError] = useState(null);
     const [isLoaded, setIsLoaded] = useState(false);
     const [items, setItems] = useState([]);
     const [dates, setDates] = useState([]);
     const [events, setEvents] = useState([]);
     const opt =[];

     const chkValue = (userId,eventId,dateId,eventUserId)=>{
         if(eventId == dateId && userId == eventUserId)
             return true
         else
             return false
     }

     const sendData = ()=>{
         debugger;
         var eventDate_id,user_id;
         var name=document.getElementById("myName").value;

         fetch("http://localhost:4449/setUsers?name="+name)
             .then(res => res.json())
             .then(
                 (result) => {
                     user_id=result[0];
                 })
         opt.map((chk) => {
             eventDate_id = chk.dateId;
             var res = fetch("http://localhost:4449/setEvents?eventDate_id=" + eventDate_id + "&user_id=" + user_id)
             console.log(res);
         })
     }

     function setChk(event,Id) {
         debugger;
         let index

         if (event.target.checked) {
             opt.push(Id)
         } else {
             index = opt.indexOf(Id)
             opt.splice(index, 1)
         }
     }

     useEffect(() => {
         fetch("http://localhost:4449/getUsers")
             .then(res => res.json())
             .then(
                 (result) => {
                     setIsLoaded(true);
                     setItems(result);
                     fetch("http://localhost:4449/getEventDates")
                         .then(res => res.json())
                         .then(
                             (result) => {
                                 setIsLoaded(true);
                                 setDates(result);
                                 fetch("http://localhost:4449/getEvents")
                                     .then(res => res.json())
                                     .then(
                                         (result) => {
                                             setIsLoaded(true);
                                             setEvents(result);
                                         })
                             })
                 },
                 (error) => {
                     setIsLoaded(true);
                     setError(error);
                 }
             )
     }, [])
          if (error) {
         return <div>Error: {error.message}</div>;
     } else if (!isLoaded) {
         return <div>Loading...</div>;
     } else {
         return (
                <div>
                    <p>This is the description of the hangout.</p>
                    <label>Name:</label>
                    <input type="text" id="myName"/>
                    <table>
                        <thead>
                        <tr>
                            <th>Name</th>
                            {
                                dates.map(date=> (
                                        <th key={date.id}>{date.eventDate} {date.eventTime}</th>
                                ))}
                        </tr>
                        </thead>
                        <tbody>
                        <tr>
                            {
                                dates.map(date=> (
                                    <td key={date.id}><input type="checkbox" onChange={(e)=>{
                                        setChk(e,date.id)
                                    }} /></td>
                                ))}
                            }
                        </tr>
                        {
                            items.map(item=> (
                                <tr key={item.id}>
                                    <td>{item.Name}</td>
                                    {dates.map(date=>(
                                    events.map(event =>(
                                            <td><input type="checkbox" checked={chkValue(item.id,event.eventDate_id,date.id,event.user_id)} disabled={true}/></td>
                                        ))))}
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <Link to="/hangoutList" onClick={sendData}>Done</Link>
                </div>
            );
        }
}
