import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import React,{ Component } from 'react';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import axios from 'axios';
import {User} from './components/API/User';
import {NewHangouts} from './components/NewHangouts/NewHangouts';
import {EventHangouts} from './components/EventHangouts/EventHangouts';

const api=axios.create({
    baseURL: `http://localhost:4449/getUserEvents`
})

class App extends Component{

    state={
        users:[]
    }

    constructor(){
        super();
        api.get('').then(res=>{
            console.log(res.data)
            this.setState({users:res.data})
        })
    }
  render(){
        return (
      <div>
        <Router>
          <switch>
              <Route path='/user' exact render={() => (<> <User/></>)}/>
              <Route path='/newHangouts' component={()=>(<> <NewHangouts/></>)}/>
              <Route path='/eventHangouts' component={()=>(<> <EventHangouts/></>)}/>
          </switch>
        </Router>
      </div>
  );
    }
}

export default App;
