import React, { useState,useEffect } from 'react'
import axios from "axios";

 export const EventHangouts =()=>{
     const [error, setError] = useState(null);
     const [isLoaded, setIsLoaded] = useState(false);
     const [items, setItems] = useState([]);
     const [dates, setDates] = useState([]);
     const [events, setEvents] = useState([]);

     useEffect(() => {
         fetch("http://localhost:4449/getUsers")
             .then(res => res.json())
             .then(
                 (result) => {
                     setIsLoaded(true);
                     setItems(result);
                 },
                 (error) => {
                     setIsLoaded(true);
                     setError(error);
                 }
             )
     }, [])

     useEffect(() => {
             fetch("http://localhost:4449/getEventDates")
                 .then(res => res.json())
                 .then(
                     (result) => {
                         debugger;
                         setIsLoaded(true);
                         setDates(result);
                     },
                     (error) => {
                         setIsLoaded(true);
                         setError(error);
                     }
                 )
     }, [])

     useEffect(() => {
         fetch("http://localhost:4449/getEvents")
             .then(res => res.json())
             .then(
                 (result) => {
                     debugger;
                     setIsLoaded(true);
                     setEvents(result);
                 },
                 (error) => {
                     setIsLoaded(true);
                     setError(error);
                 }
             )
     }, [])

          if (error) {
         return <div>Error: {error.message}</div>;
     } else if (!isLoaded) {
         return <div>Loading...</div>;
     } else {
         return (
                <div>
                    <p>This is the description of the hangout.</p>
                    <label>Name:</label>
                    <input type="text"/>
                    <table style="border:5px">
                        <thead>
                        <tr>
                            <th>Name</th>
                            {
                                dates.map(date=> (
                                        <th key={date.id}>{date.eventDate} {date.eventTime}</th>
                                ))}
                        </tr>
                        </thead>
                        <tbody>
                        {
                            items.map(item=> (
                                <tr key={item.id}>
                                    <td>{item.Name}</td>
                                    <td><input type="checkbox"/></td>
                                    <td><input type="checkbox"/></td>
                                    <td><input type="checkbox"/></td>
                                    <td><input type="checkbox"/></td>
                                    <td><input type="checkbox"/></td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                    <button>Done</button>
                </div>
            );
        }
}
